    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        //GenerateOMR Button

        // Write the No.of Questions into CONFIG.TXT file
        String strQuestion = jTextField1.getText();
        String strOptions = jTextField2.getText();
        int verRequired = 0;
        if (jCheckBox1.isSelected())
            verRequired = 1;
       
        try {
            FileWriter fw = new FileWriter("config.txt");
            fw.write(strQuestion);
            fw.write("\n");
            fw.write(strOptions);
            fw.write("\n");
            fw.write(Integer.toString(verRequired));
            fw.write("\n");
            fw.close();
        } catch (Exception e) {
            System.out.println(e);
        }

        // Create pdf with name "omr_template"
        try {
            
            OMR_GeneratePDF.createPdf();
            JOptionPane.showMessageDialog(this, "OMR Sheet generated");

            
        } catch (IOException e) {
            e.printStackTrace();
        } catch (DocumentException e) {
            e.printStackTrace();
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // Browse for OMR file
        JFileChooser c = new JFileChooser();
        String filename, dirname;
        // Demonstrate "Open" dialog:
        int rVal = c.showOpenDialog(OMR_Main.this);
        if (rVal == JFileChooser.APPROVE_OPTION) {
            jLabel17.setText(c.getSelectedFile().getName());
            filename = c.getSelectedFile().getName();
            dirname = c.getCurrentDirectory().toString();
            OMR_Process.omrFilename = dirname + "\\" + filename;   
        }
        if (rVal == JFileChooser.CANCEL_OPTION) {
            //jLabel16.setText("You pressed cancel");
            JOptionPane.showMessageDialog(this, "You pressed Cancel");
            dirname = "";
        }    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        OMR_Process.negMark = Double.valueOf(jTextField3.getText());
        OMR_Process.omrProcess();
        if(OMR_Process.validUsn)
            JOptionPane.showMessageDialog(this, "USN: " + OMR_Process.usn + "\nMarks: " +OMR_Process.finalMark);
        else
            JOptionPane.showMessageDialog(this, "USN: Incorrect Bubbling !!!\nMarks: " +OMR_Process.finalMark,"Error Message Box",JOptionPane.ERROR_MESSAGE);

    }//GEN-LAST:event_jButton3ActionPerformed

